import React from "react";
import "./App.css";
import Image1 from "./image1";
import MiddleRight from './MiddleRightSide';
import PersonalDetails from './PesronalDetails';
import { useState, useEffect } from 'react';

const useTypewriter = (text, speed = 50) => {
    const [displayText, setDisplayText] = useState('');

    useEffect(() => {
        let i = 0;
        const typingInterval = setInterval(() => {
            if (i < text.length) {
                setDisplayText(prevText => prevText + text.charAt(i));
                i++;
            } else {
                clearInterval(typingInterval);
            }
        }, speed);

        return () => {
            clearInterval(typingInterval);
        };
    }, [text, speed]);

    return displayText;
};



function Left() {
    return (
        <div className="leftBar">
            <div className="lcol ">
                <h3 className="name-title">Hello, I am Hajar Amara</h3>

                <Image1 /></div>
            <div className="mcol">
                <MiddleRight />
            </div>
            <div className=" rcol">
                <PersonalDetails />
            </div>
            <div className="sentenceLetter">
                {/* <h1>
                    Hello,{" "}
                    <Typed strings={["I'M Hajar"]} typeSpeed={100} loop />
                </h1> */}
            </div>
        </div>
    );
}

export default Left;
